mapper.select:
select * from ( select ROW_NUMBER() over (order by GOODS_NUM desc) as rn,
from goods
GOODS_NAME like '%' || #{searchWord} || '%' or GOODS_NUM like '%' || #{searchWord} || '%'
)sub1 where rn between #{startRow} and #{endRow}

mapper.delete:
delete from goods 
where goods_num in 
						#{goodsNum}

mapper.update:
 
update goods

	GOODS_NAME = #{goodsName}, GOODS_PRICE = #{goodsPrice}
	,GOODS_CONTENTS = #{goodsContents}
    ,update_emp_num = #{updateEmpNum}, goods_update_date = sysdate
    
    	,GOOBS_MAIN_IMAGE = #{goodsMainImage}
    	,GOODS_MAIN_STORE_IMAGE = #{goodsMainStoreImage}
    
    
    	,GOODS_DETAIL_IMAGE = #{goodsDetailImage}
    	,GOODS_DETAIL_STORE_IMAGE = #{goodsDetailStoreImage}
    

where goods_num = #{goodsNum}
